	$.wijchart._line.prototype.processYValues = function (values, 
			display, hole, stacked, lastValues) {
		var vals = [],
			idx = 0,
			firstYIdx = 0;
		$.each(values, function (i, value) {
			if (!idx) {
				firstYIdx = i;
			}
			var val = {
					isHole: false,
					isValue: true,
					idx: idx,
					firstYIdx: firstYIdx,
					x: 0,
					y: 0
				};
			idx++;
			
			if ($.wijchart.isHole(value, hole)) {
				if (stacked) {
					values[i] = 0;
				} else {
					val.isHole = true;
					if (display === "excludeHole") {
						idx--;
						val.idx = 0;
					} else {
						idx = 0;
						val.idx = 0;
					}
					if (!$.wijchart.isHole(hole) && value === hole) {
						val.isValue = true;
					} else {
						val.isValue = false;
					}
				}
			}
			vals.push(val);
			if (stacked && i < lastValues.length) {
				values[i] += lastValues[i];
			}
		});
		lastValues.length = 0;
		$.merge(lastValues, values);
		return vals;
	};
	
