		_laySelectedElementAttributes: function ($attriblist, $tag) {
			var self = this,
		el = self._getInspectElement(),
		dic, tag, attrs,
		html = "";

			if (!el) {
				return;
			}

			$tag.html(el.tagName);

			dic = self._getDictionaryDeclare();
			tag = el.tagName.toLowerCase();
			attrs = dic[tag];

			switch (tag) {
			case 'ul':
			case 'u':
			case 'textarea':
			case 'tbody':
			case 'tr':
			case 'th':
			case 'td':
			case 'table':
			case 'select':
			case 'strong':
			case 'span':
			case 'p':
			case 'option':
			case 'ol':
			case 'li':
			case 'label':
			case 'input':
			case 'img':
			case 'i':
			case 'hr':
			case 'form':
			case 'font':
			case 'em':
			case 'div':
			case 'code':
			case 'button':
			case 'body':
			case 'b':
			case 'a':
				html += "<ul class='ui-helper-reset'>";

				$.each(attrs, function (idx, attr) {
					var val = el.getAttribute(attr) || "";

					html += "<li><span class='" + css_dlg_text + "'>";
					html += attr + ":</span>";
					html += "<input type='text' value='" + val + "'/>";
					html += "<input type='checkbox' id='save" + idx + "'/>";
					html += "<label for='save" + idx + "'>Save</label></li>";
				});
				break;
			}

			$attriblist.html(html);
		},

