		_percentage: function () {
			var self = this,
				o = self.options,
				max = o.max,
				min = o.min,
				value = o.value;

			if (isNaN(value)) {
				return 0;
			}
			if (max === min) {
				return 1;
			}
			return (value - min) / (max - min);
		},

		_valueToLogical: function (value) {
			var self = this,
				o = self.options,
				min = o.min,
				max = o.max,
				logarithmicBase = o.logarithmicBase,
				linerValue, alpha;

			if (value < min) {
				return 0;
			}
			if (max < value) {
				return 1;
			}
			linerValue = (value - min) / (max - min);
			if (!o.islogarithmic) {
				alpha = linerValue;
			}
			else {
				alpha = $.wijgauge.math.log(1 + (logarithmicBase - 1) * 
					linerValue, o.logarithmicBase);
			}

			return alpha;
		},

		_logicalToValue: function (alpha) {
			var self = this,
				o = self.options,
				max = o.max,
				min = o.min,
				linearValue;

			if (alpha < 0) {
				return min;
			}
			if (1 <= alpha) {
				return max;
			}
			if (!o.islogarithmic) {
				linearValue = alpha;
			}
			else {
				if (o.logarithmicBase <= 1) {
					return o.min;
				}
				linearValue = (Math.pow(o.logarithmicBase, alpha) - 1) / 
					(o.logarithmicBase - 1);
				//linearValue = Math.pow(alpha, o.logarithmicBase);
			}
			return min + (max - min) * linearValue;
		},



